UPDATE WordAnalysis_word SET french_definition = '1. Partie de l''espace intérieur d''un bâtiment située immédiatement sous la toiture et séparée des parties inférieures par un plancher ou une voûte. 2. Faîte du bâtiment, comprenant charpente et couverture.     Contraires : cave - fondations' WHERE french='comble';
